import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Equipement } from './equipement.model';

@Component({
  selector: 'app-equipement',
  templateUrl: './equipement.component.html'
})
export class EquipementComponent {
  equipement: Equipement = {
    name: '',
    category: '',
    etat: '',
    dateAchat: '',
    marque: '',
    dateMaintenance: ''
  };

  constructor(private http: HttpClient) {}

  submit() {
    this.http.post('http://localhost:9091/GestionEcole/api/equipments', this.equipement)
      .subscribe({
        next: () => {
          alert('Équipement ajouté avec succès !');
          this.equipement = { name: '', category: '', etat: '', dateAchat: '', marque: '', dateMaintenance: '' };
        },
        error: (err) => {
          console.error(err);
          alert('Erreur lors de l\'ajout de l\'équipement.');
        }
      });
  }
}
<div class="container mt-4">
  <h2 class="mb-4">Gestion des Équipements</h2>

  <!-- Formulaire d'ajout/modification -->
  <form (ngSubmit)="onSubmit()" #equipementForm="ngForm" class="mb-4">
    <div class="row">
      <div class="col-md-4">
        <input type="text" [(ngModel)]="equipement.nom" name="nom" placeholder="Nom de l'équipement" class="form-control" required>
      </div>
      <div class="col-md-4">
        <input type="text" [(ngModel)]="equipement.type" name="type" placeholder="Type" class="form-control" required>
      </div>
      <div class="col-md-4">
        <input type="number" [(ngModel)]="equipement.quantite" name="quantite" placeholder="Quantité" class="form-control" required>
      </div>
    </div>
    <div class="mt-3">
      <button type="submit" class="btn btn-primary">{{ isEditMode ? 'Modifier' : 'Ajouter' }}</button>
      <button type="button" class="btn btn-secondary ms-2" (click)="resetForm()">Annuler</button>
    </div>
  </form>

  <!-- Tableau d'affichage -->
  <table class="table table-striped table-bordered">
    <thead class="table-dark">
      <tr>
        <th>Nom</th>
        <th>Type</th>
        <th>Quantité</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let eq of equipements">
        <td>{{ eq.nom }}</td>
        <td>{{ eq.type }}</td>
        <td>{{ eq.quantite }}</td>
        <td>
          <button class="btn btn-sm btn-warning me-2" (click)="editEquipement(eq)">Modifier</button>

        </td>
      </tr>
    </tbody>
  </table>
</div>
