import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Equipement } from './equipement.model';
import { formatDate } from '@angular/common';

import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import * as XLSX from 'xlsx';
import * as FileSaver from 'file-saver';

@Component({
  selector: 'app-equipement',
  templateUrl: './equipement.component.html'
})
export class EquipementComponent implements OnInit {

  equipement: Equipement = {
    name: '',
    category: '',
    etat: '',
    dateAchat: '',
    marque: '',
    dateMaintenance: ''
  };

  equipements: Equipement[] = [];
  filteredEquipements: Equipement[] = [];

  editingId: number | null = null;
  showList = false;

  // üîç Filtres
  etatFilter = '';
  categoryFilter = '';

  // üìÑ Pagination
  page = 1;
  pageSize = 5;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.loadEquipements();
  }

  toggleList() {
    this.showList = !this.showList;
    if (this.showList) {
      this.loadEquipements();
    }
  }

  loadEquipements() {
    this.http.get<Equipement[]>('http://localhost:9091/GestionEcole/api/equipments')
      .subscribe(data => {
        this.equipements = data;
        this.applyFilters();
      });
  }

  applyFilters() {
    this.filteredEquipements = this.equipements.filter(e =>
      (!this.etatFilter || e.etat === this.etatFilter) &&
      (!this.categoryFilter || e.category.toLowerCase().includes(this.categoryFilter.toLowerCase()))
    );
  }

  submit() {
    this.equipement.dateAchat = formatDate(this.equipement.dateAchat, 'yyyy-MM-dd', 'en-US');
    this.equipement.dateMaintenance = formatDate(this.equipement.dateMaintenance, 'yyyy-MM-dd', 'en-US');

    const url = this.editingId
      ? `http://localhost:9091/GestionEcole/api/equipments/${this.editingId}`
      : 'http://localhost:9091/GestionEcole/api/equipments';

    const request = this.editingId
      ? this.http.put(url, this.equipement)
      : this.http.post(url, this.equipement);

    request.subscribe({
      next: () => {
        alert(this.editingId ? '√âquipement modifi√© !' : '√âquipement ajout√© !');
        this.loadEquipements();
        this.resetForm();
      },
      error: (err) => {
        console.error('Erreur backend :', err);
        alert("Erreur lors de l'enregistrement.");
      }
    });
  }

  editEquipement(e: Equipement) {
    this.equipement = { ...e };
    this.editingId = e.id!;
    this.showList = false;
  }

  deleteEquipement(id: number) {
    if (confirm("Confirmer la suppression ?")) {
      this.http.delete(`http://localhost:9091/GestionEcole/api/equipments/${id}`)
        .subscribe(() => this.loadEquipements());
    }
  }

  resetForm() {
    this.equipement = {
      name: '',
      category: '',
      etat: '',
      dateAchat: '',
      marque: '',
      dateMaintenance: ''
    };
    this.editingId = null;
  }

  exportPDF() {
    const doc = new jsPDF();
    autoTable(doc, {
      head: [['Nom', 'Cat√©gorie', '√âtat', 'Marque', "Date d'achat", 'Date maintenance']],
      body: this.filteredEquipements.map(e => [
        e.name, e.category, e.etat, e.marque, e.dateAchat, e.dateMaintenance
      ])
    });
    doc.save('liste_equipements.pdf');
  }

  exportExcel() {
    const worksheet = XLSX.utils.json_to_sheet(this.filteredEquipements);
    const workbook = { Sheets: { '√âquipements': worksheet }, SheetNames: ['√âquipements'] };
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const data: Blob = new Blob([excelBuffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8' });
    FileSaver.saveAs(data, 'liste_equipements.xlsx');
  }
}
<section id="ajout-equipement" class="container mt-5 mb-5">
  <div class="section-title">
    <h2>√âquipements</h2>
    <p>Ajouter un nouvel √©quipement</p>
  </div>

  <!-- ‚úÖ Formulaire d'ajout ou modification -->
  <form (ngSubmit)="submit()" class="row g-3 bg-light p-4 rounded shadow">
    <div class="col-md-6">
      <label for="name" class="form-label">Nom</label>
      <input type="text" [(ngModel)]="equipement.name" name="name" class="form-control" required />
    </div>

    <div class="col-md-6">
      <label for="category" class="form-label">Cat√©gorie</label>
      <input type="text" [(ngModel)]="equipement.category" name="category" class="form-control" required />
    </div>

    <div class="col-md-6">
      <label for="etat" class="form-label">√âtat</label>
      <select [(ngModel)]="equipement.etat" name="etat" class="form-select" required>
        <option value="Disponible">Disponible</option>
        <option value="En panne">En panne</option>
        <option value="Maintenance">Maintenance</option>
      </select>
    </div>

    <div class="col-md-6">
      <label for="marque" class="form-label">Marque</label>
      <input type="text" [(ngModel)]="equipement.marque" name="marque" class="form-control" required />
    </div>

    <div class="col-md-6">
      <label for="dateAchat" class="form-label">Date d'achat</label>
      <input type="date" [(ngModel)]="equipement.dateAchat" name="dateAchat" class="form-control" required />
    </div>

    <div class="col-md-6">
      <label for="dateMaintenance" class="form-label">Date de maintenance</label>
      <input type="date" [(ngModel)]="equipement.dateMaintenance" name="dateMaintenance" class="form-control" />
    </div>

    <div class="col-12 text-end">
      <button type="submit" class="btn btn-success">
        {{ editingId ? 'üíæ Enregistrer les modifications' : '‚ûï Ajouter l\'√©quipement' }}
      </button>
    </div>
  </form>
</section>

<!-- üîç Filtres -->
<div class="container mt-5">
  <div class="row mb-3">
    <div class="col-md-6">
      <label for="etatFilter" class="form-label">Filtrer par √âtat :</label>
      <select class="form-select" id="etatFilter" [(ngModel)]="etatFilter" (change)="applyFilters()">
        <option value="">Tous</option>
        <option>Disponible</option>
        <option>En panne</option>
        <option>Maintenance</option>
      </select>
    </div>
    <div class="col-md-6">
      <label for="categoryFilter" class="form-label">Filtrer par Cat√©gorie :</label>
      <input type="text" id="categoryFilter" class="form-control" [(ngModel)]="categoryFilter" (input)="applyFilters()" />
    </div>
  </div>

  <!-- üì§ Export boutons -->
  <div class="text-end mb-3">
    <button class="btn btn-outline-danger me-2" (click)="exportPDF()">üìÑ Exporter PDF</button>
    <button class="btn btn-outline-success" (click)="exportExcel()">üìä Exporter Excel</button>
  </div>

  <!-- üìã Liste filtr√©e avec pagination -->
  <app-equipement-list
    [equipements]="filteredEquipements.slice((page - 1) * pageSize, page * pageSize)"
    (edit)="editEquipement($event)"
    (delete)="deleteEquipement($event)">
  </app-equipement-list>

  <!-- üìå Pagination -->
  <nav class="mt-4">
