import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Equipement {
  id: number;
  name: string;
  quantity: number;
  category: string;
}

@Injectable({
  providedIn: 'root'
})
export class EquipementService {
  private apiUrl = 'http://localhost:8084/equipement'; // ajuste le port selon ton microservice

  constructor(private http: HttpClient) {}

  getAllEquipements(): Observable<Equipement[]> {
    return this.http.get<Equipement[]>(`${this.apiUrl}/all`);
  }

  addEquipement(e: Equipement): Observable<Equipement> {
    return this.http.post<Equipement>(`${this.apiUrl}/add`, e);
  }

  deleteEquipement(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/delete/${id}`);
  }

  updateEquipement(e: Equipement): Observable<Equipement> {
    return this.http.put<Equipement>(`${this.apiUrl}/update`, e);
  }
}
