import { Component, OnInit } from '@angular/core';
import { EquipementService } from '../services/equipement.service';
import { Equipement } from './equipement.model';

@Component({
  selector: 'app-equipement',
  templateUrl: './equipement.component.html'
})
export class EquipementComponent implements OnInit {
  equipements: Equipement[] = [];
  equipement: Equipement = { nom: '', type: '', quantite: 0 };
  isEditMode = false;

  constructor(private equipementService: EquipementService) {}

  ngOnInit(): void {
    this.loadEquipements();
  }

  loadEquipements() {
    this.equipementService.getAllEquipements().subscribe(data => this.equipements = data);
  }

  onSubmit() {
    if (this.isEditMode && this.equipement.id) {
      this.equipementService.updateEquipement(this.equipement.id, this.equipement).subscribe(() => {
        this.loadEquipements();
        this.resetForm();
      });
    } else {
      this.equipementService.addEquipement(this.equipement).subscribe(() => {
        this.loadEquipements();
        this.resetForm();
      });
    }
  }

  editEquipement(eq: Equipement) {
    this.equipement = { ...eq };
    this.isEditMode = true;
  }

  deleteEquipement(id: number) {
    this.equipementService.deleteEquipement(id).subscribe(() => this.loadEquipements());
  }

  resetForm() {
    this.equipement = { nom: '', type: '', quantite: 0 };
    this.isEditMode = false;
  }
}
