import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Equipement } from './equipement.model';
import { formatDate } from '@angular/common';
@Component({
  selector: 'app-equipement',
  templateUrl: './equipement.component.html'
})
export class EquipementComponent {
  equipement: Equipement = {
    name: '',
    category: '',
    etat: '',
    dateAchat: '',
    marque: '',
    dateMaintenance: ''
  };

  constructor(private http: HttpClient) {}

  submit() {
    this.equipement.dateAchat = formatDate(this.equipement.dateAchat, 'yyyy-MM-dd', 'en-US');
    this.equipement.dateMaintenance = formatDate(this.equipement.dateMaintenance, 'yyyy-MM-dd', 'en-US');

    console.log('Payload:', this.equipement);

    this.http.post('http://localhost:9091/GestionEcole/api/equipments', this.equipement)
      .subscribe({
        next: () => alert('Équipement ajouté avec succès !'),
        error: (err) => {
          console.error('Erreur backend :', err);
          alert("Erreur lors de l'ajout de l'équipement.");
        }
      });
  }
<h2>Ajouter un équipement</h2>

<form (ngSubmit)="submit()">
  <label>Nom :</label>
  <input type="text" [(ngModel)]="equipement.name" name="name" required><br>

  <label>Catégorie :</label>
  <input type="text" [(ngModel)]="equipement.category" name="category" required><br>

  <label>État :</label>
  <select [(ngModel)]="equipement.etat" name="etat" required>
    <option value="Disponible">Disponible</option>
    <option value="En panne">En panne</option>
    <option value="Maintenance">Maintenance</option>
  </select><br>

  <label>Date d'achat :</label>
  <input type="date" [(ngModel)]="equipement.dateAchat" name="dateAchat" required><br>

  <label>Marque :</label>
  <input type="text" [(ngModel)]="equipement.marque" name="marque" required><br>

  <label>Date de maintenance :</label>
 <input type="date" [(ngModel)]="equipement.dateMaintenance" name="dateMaintenance"><br>

  <button type="submit">Ajouter</button>
</form>
